const Post = () => {
    const[post , setPost] =useState ({})

    useEffect(() => {

        busca('/posts/${id}', setPost)
    }, [id])

    return(
      <main className="container flex flex--center">
       <article className="cartao post">
        <h2 className="cartao_titulo">{post.title}</h2>   
       </article>
       </main>
    )
}

export default Post
